(ns repley.ui.icon
  (:require [ripley.html :as h]))

;; Icons from https://icons.radix-ui.com

(defmacro icon [path]
  `(h/html
    [:svg {:width 15 :height 15 :viewBox "0 0 15 15" :fill "none"}
     ~(if (string? path)
        [:path {:d path
                :fill "currentColor"
                :fill-rule "evenodd"
                :clip-rule "evenodd"}]
        path)]))

(defmacro define-icons [& names-and-paths]
  `(do
     ~@(for [[name-sym path] (partition 2 names-and-paths)]
         `(defn ~name-sym [] (icon ~path)))))

